mapper.select:
SELECT COUNT(*)
FROM ST_CHBOX
WHERE ST_NUM = #{st_num}
  AND CHBOX_NUM = #{chbox_num}

mapper.update:
UPDATE ST_CHBOX

SET YN_NUM = #{jdbcType=INTEGER}


SET YN_NUM = #{yn_num, jdbcType=INTEGER}

WHERE ST_NUM = #{st_num}
  AND CHBOX_NUM = #{chbox_num}

UPDATE SEARCH_KEY
SET COUNT = COUNT + 1
WHERE ST_NUM = #{st_num}
  AND SEARCH_NAME = #{search_name}

mapper.select:
SELECT NVL(COUNT(SEARCH_NUM), -1)
FROM SEARCH_KEY
WHERE ST_NUM = #{st_num}
  AND SEARCH_NAME= #{search_name}

SELECT COUNT(*)
FROM ST_SEARCH_KEY
WHERE ST_NUM = #{st_num}
  AND SEARCH_NUM = #{search_num}
