SELECT W.DAY_NUM AS DAY_NUM, W.DAY_NAME AS DAY_NAME, S.ST_NUM AS ST_NUM, O.START_TIME||' ~ '||O.END_TIME AS OPERATE_TIME , NVL2(H.HOLIDAY_NUM,'휴무', '-') AS HOLIDAY FROM WEEKDAY_LABEL W CROSS JOIN ( SELECT DISTINCT ST_NUM FROM ST_OPENCLOSE ) S LEFT JOIN ST_OPENCLOSE O ON W.DAY_NUM = O.DAY_NUM AND S.ST_NUM = O.ST_NUM LEFT JOIN ST_HOLIDAY H ON W.DAY_NUM = H.DAY_NUM AND S.ST_NUM = H.ST_NUM WHERE S.ST_NUM IN (
#{st_num}
) ORDER BY S.ST_NUM, W.DAY_NUM

		SELECT ST_NUM, ROUND(AVG(PRICE),0) AS PRICE_AVG
